import React, { Component } from 'react';
import './Profile.css';
import state from "../../redux/state";




class Profile  extends React.Component{

    state = {
        editMode: false
    }

    activateEditMode()
    {
        this.setState({editMode:true})
    }
    deactivateEditMode()

    {


        this.setState({editMode:false})

    }
    render () {
        let NewMessageElement = React.createRef();
        let NewJobElement = React.createRef();
let changeStatus = () =>
{

    let NewMessageText = NewMessageElement.current.value;
    this.props.updateStatus(NewMessageText);
}
        let changeJob = () =>
        {

            let NewJobText = NewJobElement.current.value;
            this.props.updateJob(NewJobText);
        }


return (
        <nav className='profile'>

            <div className='item'><img src={state._state.ProfileInfo[0].avatar}/></div>

            {!this.state.editMode &&<div><span onDoubleClick={this.activateEditMode.bind(this)}>{this.props.profileInfo.status}</span></div>}
            {this.state.editMode &&<div> <input onChange={changeStatus} ref={NewMessageElement} value={this.props.profileInfo.status} /></div>}
            {!this.state.editMode &&<div><span onDoubleClick={this.activateEditMode.bind(this)}>{this.props.profileInfo.job}</span></div>}
            {this.state.editMode &&<div> <input onChange={changeJob} ref={NewJobElement} value={this.props.profileInfo.job}  /></div>}
            <div> {this.state.editMode && <button onClick={this.deactivateEditMode.bind(this)} >Сохранить</button> }</div>
            <br></br>
            <div>Подписок:</div>
            <div><b>34</b></div>
            <br></br>
            <div>Подписчиков:</div>
            <div><b>155</b></div>
            <br></br>

        </nav>
)
    }
}

export default Profile;
