import React from 'react';

import './index.css';

import reportWebVitals from './reportWebVitals';

import store, {
    addPostMessage,
    subsribe,
    updatePostMessage,
    updateProfileInfo,
    updateNameText,
    updateCityText
} from "./redux/state";
import ReactDOM from "react-dom";
import {BrowserRouter} from "react-router-dom";
import App from "./App";



let rerender =(state) =>{

    ReactDOM.render(
        <BrowserRouter>
            <App  appState={store} addPost={store.addPostMessage.bind(store)} updateCityText={store.updateCityText.bind(store)} updateNameText={store.updateNameText.bind(store)} updatePostText={store.updatePostMessage.bind(store)} updateProfileInfo={store.updateProfileInfo.bind(store)} />
        </BrowserRouter>,
        document.getElementById('root')

    );
}
rerender(store.getState());

store.subsribe(rerender);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
