export const wallreducer = (state, action) => {

    if (action.type === 'ADD-POST') {
        let newPost =
            {
                id: 5,
                message: state.newTextPost,
                likesCount: 0,
                name: state.ProfileInfo[0].name,
                img: state.ProfileInfo[0].avatar
            };

        state.postData.push(newPost);
        state.newTextPost = 'Новое сообщение';

    } else if (action.type === 'UPDATE-TEXT-POST') {

        state.newTextPost = action.newText;

    }
return state;
}
