const UP_STATUS_TEXT= 'UP_STATUS_TEXT';
const UP_JOB='UP_JOB';

let InitialState = {
    status: 'Статус',
    job:'Работа',
}


const ProfileReducer = (state = InitialState, action) =>
{
    switch (action.type)
    {
        case UP_STATUS_TEXT:
        {
          let copyState= {...state};
          copyState.status = action.newTextMessage;
          return copyState;
        }
        case UP_JOB:
        {
            let copyState={...state};
            copyState.job =action.NewTextJob;
            return copyState;
        }
        default:
            return state;
    }

}

export default ProfileReducer;


export const UpdateStatusAC = (newTextMessage) => ({type: UP_STATUS_TEXT, newTextMessage});
export const UpdateJobAC = (NewTextJob) => ({type:UP_JOB, NewTextJob});
