import UsersProfile from "./UsersProfile";
import {connect} from "react-redux";
import * as axios from 'axios';
import {setCurrentPageAC} from "../../redux/reducer/usersreducer";
import {ShowUserAC} from "../../redux/reducer/usersprofilereducer";



let mapStateToProps = (state) =>
{
    axios.get(`https://social-network.samuraijs.com/api/1.0/profile/2`)
        .then(response => {
            this.props.ShowUserAC(response.data.items);
        });

            return {
        id: state.UsersProfile.userProfie.id,
        fullName: state.UsersProfile.userProfie.fullName,
        ava: state.UsersProfile.userProfie.ava,

    }

}
let mapDispatchToProps = (dispatch) =>
{
    return{
        ShowUserAC: (UserInfo) =>
        {
            dispatch(ShowUserAC(UserInfo));
        },

    }
}

const UsersProfileC = connect (mapStateToProps,mapDispatchToProps) (UsersProfile);

export default UsersProfileC;
