const UP_STATUS_TEXT= 'UP_STATUS_TEXT';
const UP_JOB='UP_JOB';

let InitialState = {
    status: 'STATUS',
    job:'job',
}


const ProfileReducer = (state = InitialState, action) =>
{
    switch (action.type)
    {
        case UP_STATUS_TEXT:
        {
          let copyState= {...state};
          copyState.status = action.newTextMessage;
          return copyState;
        }
        case UP_JOB:
        {
            let copyState={...state};
            copyState
        }
        default:
            return state;
    }
return state;
}

export default ProfileReducer;


