import React from 'react';
import classes from './Post.module.css';
let postData = [
    {id: 1, message:'How are you?', likesCount:'11'},
    {id: 2, message:'Its my first post', likesCount:'23'},

]
const Post = (props) => {
    return(

    <div className={classes.content}>




        <div><textarea></textarea></div>
        <div><button>ADD POST</button></div>

        <div><Post message={postData[0].message} like={postData[0].likesCount} ava='https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQCENkSHv5ZkAb030gCJYy2i8G5mZDPpZpOWw&usqp=CAU'/></div>
        <div><Post message={postData[1].message} like={postData[1].likesCount} ava='https://sun9-26.userapi.com/impf/c846520/v846520497/143c47/BJnz-LBzzOo.jpg?size=200x0&quality=90&crop=1059,169,899,1269&sign=6c20964b305f6dc10933ab8e3fdacdda&ava=1'/></div>


        <div className={classes.item}><img src={props.ava}></img>{props.message}</div>
          
          <div></div><span>Like {props.like}</span>
          
      </div>
    )
}

export default Post;
