import {BrowserRouter} from "react-router-dom";
import App from "./App";



export let rerender =() =>{

    ReactDOM.render(
        <BrowserRouter>
            <App  appState={state} addPost={addPostMessage} updatePostText={updatePostMessage} />
        </BrowserRouter>,
        document.getElementById('root')

    );
}
rerender();

