const ADD_POST = 'ADD-POST';
const UPD_TEXT_POST = 'UPDATE-TEXT-POST';


let initialState = {

    postData: [
        {
            id: 1,
            message: 'Как твои дела?',
            likesCount: '11',
            name: 'Скарлет Йоханссон',
            img: 'https://thumbs.dfs.ivi.ru/storage33/contents/f/b/722ce30ba1a228f9d1aa5650db3aea.jpg'
        },
        {
            id: 2,
            message: 'Привет!',
            likesCount: '2',
            name: 'Elven Bacon',
            img: 'https://sun9-5.userapi.com/impf/c840133/v840133948/4ddf1/SKM1-SBxrrc.jpg?size=200x0&quality=96&crop=784,33,746,1096&sign=0ef041bc6a0a369e414da4a3d70af83f&ava=1'
        },
        {
            id: 3,
            message: 'Это мой первый пост!',
            likesCount: '23',
            name: 'Элизабет Олсен',
            img: 'https://www.film.ru/sites/default/files/people/1576149-832820.jpg'
        },

    ],
    newTextPost: 'Новое сообщение'
};



const wallreducer = (state = initialState, action) => {


    switch(action.type)
    {
        case ADD_POST:
            let firstname = state.ProfileInfo[0].Firstname;
            let secondname = state.ProfileInfo[0].Secondname;
            let newPost =
                {

                                id: 5,
                    message: state.WallDatas.newTextPost,
                    likesCount: 0,
                    name: firstname +' '+ secondname,
                    img: state.ProfileInfo[0].avatar
                };

            state.WallDatas.postData.push(newPost);
            state.WallDatas.newTextPost = 'Новое сообщение';
            return state;

        case UPD_TEXT_POST:

           state.WallDatas.newTextPost = action.newText;
            return state;
        default:
            return state;

    }




}
export default wallreducer;
