import React from 'react';
import classes from './Post.module.css';




const Post = (props) => {
let onChange = () =>
{
    props.likesCount = props.likesCount +1;
}
    return(

    <div className={classes.content}>






        <div className={classes.item}><img  src={props.img}/>{props.name}</div>
        <div >{props.message}</div>
        <div className={classes.item.like}><img onClick={onChange} width="15" src='https://c0.klipartz.com/pngpicture/540/262/gratis-png-corazon-blanco-redes-sociales-facebook-como-boton-corazon-emoticon-facebook-vivo-amor-thumbnail.png'></img>{props.likesCount}</div>


      </div>
    )
}

const Posts = (props) =>{
    let postElement = props.WallDatas.postData.map (p => <Post img={p.img} name={p.name} message={p.message} likesCount={p.likesCount}/>);
    return (
        <div>
        {postElement}
        </div>
    )
}

export default Posts;
