const ADD_POST = 'ADD-POST';
const UPD_TEXT_POST = 'UPDATE-TEXT-POST';


let initialState = {


        postData: [
            {
                id: 1,
                message: 'Как твои дела?',
                likesCount: '11',
                name: 'Скарлет Йоханссон',
