                img: 'https://www.film.ru/sites/default/files/people/1576149-832820.jpg'
            },

        ],
        newTextPost: 'Новое сообщение'

}



const wallreducer = (state = initialState , action) => {


    switch(action.type)
    {
        case ADD_POST:
            let firstname = 'Саша'
            let secondname = 'Малаахов';
            let newPost =
                {

                                id: 5,
                    message: state.newTextPost,
                    likesCount: 0,
                    name: firstname +' '+ secondname,
                    img: ''
                };

            state.postData.push(newPost);
            state.newTextPost = 'Новое сообщение';
            return state;

        case UPD_TEXT_POST:

           state.newTextPost = action.newText;
            return state;
        default:
            return state;

    }




}
export default wallreducer;
