import React from 'react';
import classes from './Post.module.css';
import state from "../../../redux/state";



const Post = (props) => {

    return(

    <div className={classes.content}>






        <div className={classes.item}>{props.message}.{props.likesCount}</div>



      </div>
    )
}

const Posts = (props) =>{
    let postElement = state.postData.map(p => <Post message={p.message} likesCount={p.likesCount}/>);
    return (
        <div>
        {postElement}
        </div>
    )
}

export default Posts;
