import React from 'react';

import {UpdateMessageTextCreator} from "../../redux/state";
import {AddMessageCreator} from "../../redux/state";
import Dialogs from "./Dialogs";


const DialogsContainer = (props) => {


        let addMessage = () => {
        props.dispatch(AddMessageCreator());
    }

    let UpdateMessageText = (text) => {

        props.dispatch(UpdateMessageTextCreator(text));
    }

<Dialogs addMessage={addMessage} UpdateMessageText={UpdateMessageText} MessengerData={props.appState.MessengerData}/>
}

export default DialogsContainer;
