import React from 'react';

import './index.css';

import reportWebVitals from './reportWebVitals';

import store, {
    addPostMessage,
    subsribe,
    updatePostMessage,
    updateProfileInfo,
    updateNameText,
    updateCityText
} from "./redux/state";
import ReactDOM from "react-dom";
import {BrowserRouter} from "react-router-dom";
import App from "./App";



let rerender =() =>{

    ReactDOM.render(
        <BrowserRouter>
            <App  appState={store.getState()} addPost={store.addPostMessage} updateCityText={store.updateCityText}updateNameText={store.updateNameText} updatePostText={store.updatePostMessage} updateProfileInfo={store.updateProfileInfo} />
        </BrowserRouter>,
        document.getElementById('root')

    );
}
rerender(store._state);

store.subsribe(rerender);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
