import React from 'react';
import classes from './Content.module.css';
import Posts from './Posts/Post';
import {addPostActionCreator} from "../../redux/state";


const Content = (props) => {
    let newPostElement = React.createRef();
    let addPost = () =>
    {

       props.dispatch(addPostActionCreator());
       newPostElement.current.value = '';
    }
    let postChange = () =>
    {
let text = newPostElement.current.value;
        props.dispatch();
        }
    let onClickText = () =>
    {
        props.dispatch({type:'CLICK-TEXT-POST'})
    }
    return(
 
    <div className={classes.content}>
        

       
        <div className={classes.item}>MY POST</div>
        <div><textarea onClick={onClickText} onChange={postChange} ref={newPostElement} value={props.WallData.newTextPost}/></div>
        <div><button onClick={addPost}>ADD POST</button></div>

        <div><Posts  postData={props.WallData.postData}/></div>


          
      </div>
    )
}

export default Content;
