import React from 'react';
import classes from './Dialogs.module.css';
import { NavLink } from 'react-router-dom';



const DialogItem = (props) =>
{
    return(
        <div><NavLink className={classes.item} activeClassName={classes.active} to={'/dialogs/'+props.id}>{props.name}</NavLink></div>
    )
}

const Message = (props) => {
    return(

        <div> {props.message} </div>


    )
}



let MessageData = [
    {id: 1, message: 'Hi'},
    {id: 2, message: 'How are you?'},
    {id: 3, message: 'Yo'},
    {id: 4, message: 'ReactJS'},
    {id: 5, message: 'IT-Kamasutra'},
]

let messageElement = MessageData.map(m => <Message message={m.message} />);


const Dialogs = (props) => {

    let dialogElement = props.DialogsData.map(d => <DialogItem name={d.name} id={d.id} />);

return(

    <div className={classes.dialogs}>

        <div className={classes.dialogs.item}>
            <b>


                <div>{ dialogElement }</div>
</b>
        </div>
        <div>

           {messageElement}
  </div>

    </div>

)
}

export default Dialogs;
