import React, { Component } from 'react';
import './Profile.css';
import state from "../../redux/state";




class Profile  extends React.Component{

    state = {
        editMode: false
    }

    activateEditMode()
    {
        this.setState({editMode:true})
    }
    deactivateEditMode()

    {
        let NewMessageElement = React.createRef();
        let NewMessageText = NewMessageElement.current.value;
        this.setState({editMode:false})
        this.props.updateStaus(NewMessageText)
    }
    render () {




return (
        <nav className='profile'>

            <div className='item'><img src={state._state.ProfileInfo[0].avatar}/></div>

            {!this.state.editMode &&<div><span onDoubleClick={this.activateEditMode.bind(this)}>{this.props.profileInfo.status}</span></div>}
            {this.state.editMode &&<div> <input ref={NewMessageElement} value={this.props.profileInfo.status} autoFocus={true} onBlur={this.deactivateEditMode.bind(this)}/></div>}
            <div> {state._state.ProfileInfo[0].job}</div>
            <div> {state._state.ProfileInfo[0].city}</div>
            <br></br>
            <div>Подписок:</div>
            <div><b>34</b></div>
            <br></br>
            <div>Подписчиков:</div>
            <div><b>155</b></div>
            <br></br>

        </nav>
)
    }
}

export default Profile;
