import React from 'react';

import './index.css';

import reportWebVitals from './reportWebVitals';

import store from "./redux/redux-store";
import ReactDOM from "react-dom";
import {BrowserRouter} from "react-router-dom";
import App from "./App";



let rerender =(state) =>{

    ReactDOM.render(
        <BrowserRouter>
            <App  appState={state} dispatch={store.dispatch.bind(store)} updateCityText={store.updateCityText.bind(store)} updateNameText={store.updateNameText.bind(store)}  updateProfileInfo={store.updateProfileInfo.bind(store)} />
        </BrowserRouter>,
        document.getElementById('root')

    );
}
rerender(store.getState());

store.subscribe(()=>{
    let state = store.getState();
    rerender(state);
});


reportWebVitals();
