import {rerender} from "../render";


let state = {

        DialogsData: [
            {id: 1, name: 'Sasha'},
            {id: 2, name: 'Polina'},
            {id: 3, name: 'Mark'},
            {id: 4, name: 'Pavel'},
            {id: 5, name: 'Bill'},
        ],
        MessageData: [
            {id: 1, message: 'Привет'},
            {id: 2, message: 'Как дела?'},
            {id: 3, message: 'Yo'},
            {id: 4, message: 'ReactJS'},
            {id: 5, message: 'IT-Kamasutra'},
        ],
        postData: [
            {id: 1, message: 'Как твои дела?', likesCount: '11'},
            {id: 2, message: 'Это мой первый пост!', likesCount: '23'},
            {id: 3, message: 'Это мой первый пост!', likesCount: '23'},

        ]


}
export let addPostMessage = (postMessage) =>
{
    rerender();
    let newPost ={
        id: 5,
        message: postMessage,
        likesCount: 0
    };
    state.postData.push(newPost);

}


export default state;
