import React from 'react';
import classes from './Settings.module.css';
import state from "../../redux/state";
import Profile from "../Profile/Profile";
import {rerender} from "../../render";


let textname = React.createRef();
export let push = () =>
{
    state.ProfileInfo[0].name = 'Piska';
 rerender();

}
const Settings = (props) => {
return(
    <div className={classes.item}>SETTINGS
    <div>Имя<textarea ref={textname}></textarea></div>
        <div>Город<textarea></textarea></div>
        <div>Профессия<textarea></textarea></div>

<button onClick={push}>СОХРАНИТЬ</button>

    </div>

    

)
}

export default Settings;
