import React from 'react';
import classes from './Settings.module.css';
import state from "../../redux/state";
import {rerender} from "../../index";



let textname = React.createRef();
let textcity = React.createRef();
let textjob = React.createRef();
export let push = () =>
{

    state.ProfileInfo[0].city = textcity.current.value;
    state.ProfileInfo[0].job = textjob.current.value;

  Settings.props.name = textname.current.value;

}
const Settings = (props) => {
return(
    <div className={classes.item}>SETTINGS
    <div>Имя<textarea ref={textname}>Name</textarea></div>
        <div>Город<textarea ref={textcity}>City</textarea></div>
        <div>Профессия<textarea ref={textjob}>Job</textarea></div>

<button onClick={push}>СОХРАНИТЬ</button>

    </div>

    

)
}

export default Settings;
