import React from 'react';


import {addPostActionCreator, UpdateTextPostActionCreator} from "../../redux/state";
import Content from "./Content";
import {connect} from "react-redux";
import StoreContext from "../../StoreContext";


const ContentContainer = () => {



    return (
        <StoreContext.Consumer>{
            (store) => {
                let addPost = () => {

                    store.dispatch(addPostActionCreator());

                }
                let postChange = (text) => {


                    store.dispatch(UpdateTextPostActionCreator(text));
                }
                return (
                    <Content UpdateNewText={postChange} AddPost={addPost} WallData={store.getState().WallDatas}/>)
            }
        }
        </StoreContext.Consumer>
    )

}
let mapStateToProps = (state) =>
{
    return {
        WallData: state.WallDatas
    }

}
let mapDispatchToProps = (dispatch) =>
{
    return{
UpdateNewText : (text)=>
{
    dispatch(UpdateTextPostActionCreator(text));
},
        AddPost : () =>
        {
            dispatch(addPostActionCreator());
        }
    }
}

const SuperContentContainer = connect (mapStateToProps,mapDispatchToProps) (Content);

export default ContentContainer;
