    return(
        <div><NavLink className={classes.item} activeClassName={classes.active} to={'/dialogs/'+props.id}>{props.name}</NavLink></div>
    )
}

const Message = (props) => {
    return(

        <div> {props.message} </div>


    )
}







const Dialogs = (state) => {
    let messageElement = state.MessengerData.MessageData.map (m => <Message message={m.message} />);
    let dialogElement = state.MessengerData.DialogsData.map (d => <DialogItem name={d.name} id={d.id} />);

return(

    <div className={classes.dialogs}>

        <div className={classes.dialogs.item}>
            <b>


                <div>{ dialogElement }</div>
</b>
        </div>
        <div>

           {messageElement}
  </div>
<div><textarea ref={NewMessageElement}></textarea><button onClick={addMessage}></button> </div>
    </div>

)

}

export default Dialogs;
