import React from 'react';
import classes from './Post.module.css';

let postData = [
    {id: 1, message:'Как твои дела?', likesCount:'11'},
    {id: 2, message:'Это мой первый пост!', likesCount:'23'},

]

const Post = (props) => {

    return(

    <div className={classes.content}>






        <div className={classes.item}>{props.message}</div>



      </div>
    )
}

const Posts = (props) =>{
    let postElement = postData.map(p => <Post message={p.message} />);
    return (
        <div>
        {postElement}
        </div>
    )
}

export default Posts;
